import {useEffect,React} from "react";
import { WeatherProvider, useWeather } from "./components/WeatherContext";
import SearchBar from "./components/SearchBar";
import One from "./components/One";
import Five from "./components/Five";
import gif from './components/logo5.webp';

function App() {
  const { weather } = useWeather();

  useEffect(() => {
    if (weather && weather.weather) {
      const { weather: currentWeather } = weather;
      if (currentWeather && Array.isArray(currentWeather) && currentWeather.length > 0) {
        console.log("Weather Description of Current City:", currentWeather[0].description);
      }
    }
  }, [weather]);


  return (
    <WeatherProvider>
      <div
        style={{
          width: '800px',
          margin: '0 auto',
          position: 'relative',
          backgroundImage: `url(${gif})`,
          backgroundSize: 'cover',
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'center',
          width: '60%',
          height: '10%',
        }}
        className="mt-4 py-5 px-16 h-fit shadow-xl shadow-gray-400"
      >
        <div
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            backgroundColor: 'rgba(42, 42, 42, 0.4) ',
          }}
        />
        <SearchBar />
        <One />
        <Five />
        {/* <Four /> */}
      </div>
    </WeatherProvider>
  );
}

export default App;
