import React from 'react';
import { useWeather } from './WeatherContext'; // Adjust the import path if needed
import { UilTemperature, UilClouds, UilSun, UilSunset, UilWind, UilTear, UilLocationPoint } from "@iconscout/react-unicons";

const One = () => {
  const { weather } = useWeather();

  return (
    <div className='p-7 m-7 bg-red-300'>
      {weather ? (
        <>
<div className='text-center text-2xl text-white m-4'>
  {new Date(weather.dt * 1000).toLocaleDateString('en-US', { weekday: 'long' })}, {new Date(weather.dt * 1000).toLocaleDateString()} {new Date(weather.dt * 1000).toLocaleTimeString()}
</div>

          <div className="flex justify-between text-white text-lg items-center">
            <div className="p-9 text-4xl">{weather.name}</div>
            <div  className=" text-4xl">{weather.main.temp}°C</div>
          </div>
          <div className="flex flex-col justify-center items-center m-4 p-4">
            <div><img src={`http://openweathermap.org/img/wn/${weather.weather[0].icon}.png`} alt="Weather Icon" /></div>
            <div className="text-white text-2xl">
              {weather.weather[0].description}
            </div>
          </div>
          <div className="flex justify-between text-white text-md items-center">
            <div>
              <UilSunset size={24} className="mr-1" />
              {new Date(weather.sys.sunset * 1000).toLocaleTimeString()}
            </div>
            <div>
              <UilSun size={24} className="mr-1" />
              {new Date(weather.sys.sunrise * 1000).toLocaleTimeString()}
            </div>
            <div>
              <UilTear size={24} className="mr-1" />
              {weather.main.humidity}%
            </div>
            <div>
              <UilWind size={24} className="mr-1" />
              {weather.main.feels_like}°C
            </div>
            <div>
              <UilLocationPoint size={24} className="mr-1" />
              {weather.wind.speed} km/h
            </div>
          </div>
        </>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default One;
