import "./App.css";
import Three from "./components/Three";
import Merge from "./components/Merge";
import Four from "./components/Four";

function fetchWeather(country, apiKey) {
  return fetch(
    `https://api.openweathermap.org/data/2.5/weather?q=${country}&units=metric&appid=${apiKey}`
  )
    .then((response) => response.json())
    .then((data) => {
      console.log(data); // Log the weather data to the console
      return data;
    })
    .catch((error) => {
      console.error("Error fetching weather data:", error);
      return null;
    });
}

console.log(fetchWeather("Islamabad", "baafed74ddd9f05c52673a3bd435bb33"));

function App() {
  return (
    <>
    <div className="fixed inset-0 bg-gradient-to-br from-blue-100 via-blue-200 to-blue-300 shadow-lg m-7 p-4">
    <div className="max-w-md mx-auto p-4">
    <div className="relative flex items-center">
        <input type="search" id="default-search" className="block w-full p-2 text-sm text-gray-900 border border-gray-300 rounded-lg placeholder-gray-400 focus:outline-none" placeholder="Search city here..." required />
        <button type="submit" className="text-white bg-green-500 hover:bg-blue-500 font-medium rounded-lg text-sm px-4 py-2 ml-2">Search</button>
    </div>
</div>



      <div className="App flex flex-row bg-red-300">
        <div className="w-1/2 p-4">
          <Merge />
        </div>
        <div className="w-1/2 p-4">
          <Three />
        </div>
      </div>
      <Four />
</div>


    </>
  );
}

export default App;
