import React, { useEffect } from 'react';

const One = () => {
  useEffect(() => {
    const loadWeatherWidget = () => {
      const script = document.createElement('script');
      script.async = true;
      script.charset = 'utf-8';
      script.src = '//openweathermap.org/themes/openweathermap/assets/vendor/owm/js/weather-widget-generator.js';
      const s = document.getElementsByTagName('script')[0];
      s.parentNode.insertBefore(script, s);

      window.myWidgetParam ? window.myWidgetParam : window.myWidgetParam = [];
      window.myWidgetParam.push({
        id: 1,
        cityid: '1162015', // Change this to your desired city ID
        appid: 'baafed74ddd9f05c52673a3bd435bb33', // Replace with your actual API key
        units: 'metric',
        containerid: 'openweathermap-widget-1',
      });

      return () => {
        // Clean up the script when the component is unmounted
        document.getElementById('openweathermap-widget-1').innerHTML = '';
      };
    };

    loadWeatherWidget();
  }, []);

  return (
    <div id="openweathermap-widget-1"></div>
  );
};

export default One;
