import React from "react";
import { WeatherProvider, useWeather } from "./components/WeatherContext";
import SearchBar from "./components/SearchBar";
import One from "./components/One";
import Five from "./components/Five";
import gif from './components/logo8.jpeg';

function App() {
  // const { weather } = useWeather(); // Access the weather data from the context

  return (
    <WeatherProvider>
      <div
        style={{
          width: '800px',
          margin: '0 auto',
          position: 'relative',
          // backgroundImage: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
          backgroundImage: `url(${gif})`,
          backgroundSize: 'cover',
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'center',
          width: '60%',
          height: '10%',
        }}
        className="mt-4 py-5 px-16 h-fit shadow-xl shadow-gray-400"
      >
        <div
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            backgroundColor: 'rgba(42, 42, 42, 0.2) ',
          }}
        />
        <SearchBar />
        <One />
        <Five />
      </div>
    </WeatherProvider>
  );
}

export default App;
