import React, { useState, useEffect } from 'react';

function fetchWeather(country, apiKey) {
  return fetch(`https://api.openweathermap.org/data/2.5/weather?q=${country}&appid=${apiKey}`)
      .then(response => response.json())
      .then(data => {
          console.log(data); // Log the weather data to the console
          return data;
      })
      .catch(error => {
          console.error('Error fetching weather data:', error);
          return null;
      });
}

const Three = () => {
  const [weatherData, setWeatherData] = useState(null);

  useEffect(() => {
    fetchWeather('Islamabad', 'baafed74ddd9f05c52673a3bd435bb33')
      .then(data => setWeatherData(data));
  }, []);

  return (
    <div className='p-4 m-4 bg-blue-300'>
      {weatherData && (
        <div>
          <h2>Weather in London</h2>
          <p>Temperature: {weatherData.main.temp}Â°C</p>
          <p>Description: {weatherData.weather[0].description}</p>
        </div>
      )}
    </div>
  );
}

export default Three;
