import React from 'react';
import { useWeather } from './WeatherContext'; // Adjust the import path if needed
import { UilTemperature, UilClouds, UilSun, UilSunset, UilWind, UilTear, UilLocationPoint } from "@iconscout/react-unicons";

const One = () => {
  const { weather } = useWeather();

  return (
    <div className='p-4 m-4 bg-gradient-to-br from-blue-700 to-cyan-700'>
      {weather ? (
        <>
          <div className="flex justify-between text-white text-md items-center">
            <div>
              {new Date(weather.dt * 1000).toLocaleDateString()} {new Date(weather.dt * 1000).toLocaleTimeString()}
            </div>
            <div className="font-bold text-lg">{weather.name}</div>
            <div>{weather.main.temp}°C</div>
          </div>
          <div className="flex justify-center items-center">
            <img src={`http://openweathermap.org/img/wn/${weather.weather[0].icon}.png`} alt="Weather Icon" />
            <div>{weather.weather[0].description}</div>
          </div>
          <div className="flex justify-between text-white text-md items-center">
            <div>Sunset: {new Date(weather.sys.sunset * 1000).toLocaleTimeString()}</div>
            <div>Sunrise: {new Date(weather.sys.sunrise * 1000).toLocaleTimeString()}</div>
            <div>Humidity: {weather.main.humidity}%</div>
            <div>Real feel: {weather.main.feels_like}°C</div>
            <div>Wind: {weather.wind.speed} km/h</div>
          </div>
        </>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default One;
