import React from 'react';
import { useWeather } from './WeatherContext'; // Adjust the import path if needed
import CurrentLocation from './CurrentLocation';

const Three = () => {
  const { weather } = useWeather();

  return (
    <div className='p-4 m-4 bg-blue-300 city' style={{ backgroundImage: 'url("./images/city.jpg")', backgroundRepeat: 'no-repeat', backgroundSize: 'cover', WebkitBackgroundSize: 'cover', MozBackgroundSize: 'cover', OBackgroundSize: 'cover', minHeight: '500px', position: 'relative', backgroundPositionX: '-30px' }}>
      {weather ? (
        <>
          <h2>Weather in {weather.name}</h2>
          <p>Temperature: {weather.main.temp}째C</p>
          <p>Description: {weather.weather[0].description}</p>
          <p>Feels like: {weather.main.feels_like}째C</p>
          <p>Minimum Temperature: {weather.main.temp_min}째C</p>
          <p>Maximum Temperature: {weather.main.temp_max}째C</p>
          <p>Pressure: {weather.main.pressure} hPa</p>
        </>
      ) : (
        <p><CurrentLocation/></p>
      )}
    </div>
  );
}

export default Three;
