import React, { useEffect } from "react";
import { WeatherProvider, useWeather } from "./components/WeatherContext";
import SearchBar from "./components/SearchBar";
import One from "./components/One";
import Five from "./components/Five";
import gif from './components/logo5.webp';

function App() {
  const { weather } = useWeather();

  useEffect(() => {
    if (weather && weather.weather && Array.isArray(weather.weather) && weather.weather.length > 0) {
      console.log("Weather Description:", weather.weather[0].description);
    }
  }, [weather]);

  let backgroundPic = gif; // Default background pic
  if (weather && weather.weather && Array.isArray(weather.weather) && weather.weather.length > 0) {
    const description = weather.weather[0].description.toLowerCase();
    if (description.includes('rain')) {
      backgroundPic = 'url(./components/logo6.jpeg)';
    } else if (description.includes('cloud')) {
      backgroundPic = 'url(PATH_TO_CLOUDY_IMAGE)';
    } else if (description.includes('clear')) {
      backgroundPic = 'url(PATH_TO_CLEAR_IMAGE)';
    }
  }

  return (
    <WeatherProvider>
      <div
        style={{
          width: '800px',
          margin: '0 auto',
          position: 'relative',
          backgroundImage: backgroundPic,
          backgroundSize: 'cover',
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'center',
          width: '60%',
          height: '10%',
        }}
        className="mt-4 py-5 px-16 h-fit shadow-xl shadow-gray-400"
      >
        <div
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            backgroundColor: 'rgba(42, 42, 42, 0.2) ',
          }}
        />
        <SearchBar />
        <One />
        <Five />
      </div>
    </WeatherProvider>
  );
}

export default App;
