import React, { useEffect, useState } from "react";
import {
  UilTemperature,
  UilTear,
  UilWind,
  UilSun,
  UilSunset,
} from "@iconscout/react-unicons";

const TemperatureAndDetails = () => {
  const [weatherForecast, setWeatherForecast] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const apiKey = "baafed74ddd9f05c52673a3bd435bb33";
      const lat = "44.34";
      const lon = "10.99";
      const url = `https://api.openweathermap.org/data/2.5/forecast?lat=${lat}&lon=${lon}&appid=${apiKey}`;

      try {
        const response = await fetch(url);
        const data = await response.json();
        setWeatherForecast(data);
      } catch (error) {
        console.error("Error fetching data: ", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="bg-yellow-500">
      {weatherForecast ? (
        <div className="flex flex-row items-center justify-center py-3">
          <div className="flex flex-col space-x-4">
            <div className="flex font-light text-sm items-center justify-center text-white">
              <UilTemperature size={18} className="mr-1 text-white" />
              Real feel: {weatherForecast.list[0].main.feels_like}Â°C
            </div>
            <div className="flex font-light text-sm items-center justify-center text-white">
              <UilTear size={18} className="mr-1 text-white" />
              Humidity: {weatherForecast.list[0].main.humidity}%
            </div>
            <div className="flex font-light text-sm items-center justify-center text-white">
              <UilWind size={18} className="mr-1 text-white" />
              Wind: {weatherForecast.list[0].wind.speed} km/h
            </div>
            <div className="flex font-light text-sm items-center justify-center text-white">
              <UilSun size={18} className="mr-1 text-white" />
              Sunrise: {new Date(weatherForecast.city.sunrise * 1000).toLocaleTimeString()}
            </div>
            <div className="flex font-light text-sm items-center justify-center text-white">
              <UilSunset size={18} className="mr-1 text-white" />
              Sunset: {new Date(weatherForecast.city.sunset * 1000).toLocaleTimeString()}
            </div>
          </div>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default TemperatureAndDetails;
