import './App.css';
import Three from './components/Three';
import Merge from './components/Merge';

function showWeather(country, apiKey) {
  fetch(`https://api.openweathermap.org/data/2.5/weather?q=${country}&appid=${apiKey}`)
      .then(response => response.json())
      .then(data => {
          console.log(data); // Log the weather data to the console
          // Display the weather information on your webpage
          const weatherInfo = `Weather in ${data.name}: ${data.weather[0].description}, Temperature: ${data.main.temp} °C`;
          document.getElementById('weather-info').innerText = weatherInfo;
      })
      .catch(error => {
          console.error('Error fetching weather data:', error);
          // Display an error message on your webpage
          document.getElementById('weather-info').innerText = 'Failed to fetch weather data';
      });
}
console.log(showWeather('pakistan', 'baafed74ddd9f05c52673a3bd435bb33'));
// Example usage: showWeather('Pakistan', 'baafed74ddd9f05c52673a3bd435bb33');

function App() {
  return (
    <div className="App flex flex-row bg-red-300">
      <div className="w-1/2 p-4">
        <Merge />
      </div>
      <div className="w-1/2 p-4">
        <Three />
      </div>
    </div>
  );
}

export default App;
