import React, { useEffect ,useState} from "react";
// import { WeatherProvider, useWeather } from "./components/WeatherContext";
import { useWeather } from "./components/WeatherContext";
import SearchBar from "./components/SearchBar";
import One from "./components/One";
import Five from "./components/Five";
import gif from './components/logo5.webp';
import rainyImage from "./components/logo6.jpeg";
import cloudyImage from "./components/logo7.jpeg";
import simple from "./components/logo8.jpeg";
import p from "./components/rain2.gif";

function App() {
  const { weather } = useWeather();

  const [backgroundPic, setBackgroundPic] = useState(`url(${p})`); // Default background pic

  console.log("Hehfs",weather);
  useEffect(() => {
    if (weather) {
      // console.log("Weather Description:", weather.weather[0].description);
      console.log("HI");
      console.log("Hello",weather);
    }
    if (weather) {
      const description = weather.weather[0].description.toLowerCase();
      if (description.includes('few clouds')) {
        setBackgroundPic(`url(${cloudyImage})`);
      } else if (description.includes('mist')) {
        setBackgroundPic(`url(${simple})`);
      } else if (description.includes('clear')) {
        setBackgroundPic(`url(${rainyImage})`);
      }
      else
      {
        setBackgroundPic(`url(${p})`);
      }
    }
  }, [weather]);
  useEffect(() => {
  console.log("hi");
  });

  // let backgroundPic = rainyImage; // Default background pic


  return (
    // <WeatherProvider>
      <div
        style={{
          width: '800px',
          margin: '0 auto',
          position: 'relative',
          backgroundImage: backgroundPic,

          // backgroundImage: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
          backgroundSize: 'cover',
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'center',
          width: '60%',
          height: '10%',
        }}
        className="mt-4 py-5 px-16 h-fit shadow-xl shadow-gray-400"
      >
        <div
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            // backgroundColor: 'rgba(42, 42, 42, 0.6) ',
            opacity: 0.5,
          }}
        />
        <SearchBar />
        <One />
        <Five />
      </div>
    // </WeatherProvider>
  );
}

export default App;
