import React, { useState, useEffect } from 'react';
import fetchWeatherByCityName from './LocationByCityName';

const Three = () => {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);

  useEffect(() => {
    if (city.trim() !== '') {
      async function fetchData() {
        const data = await fetchWeatherByCityName(city);
        setWeather(data);
      }
      fetchData();
    }
  }, [city]);

  return (
    <div className='p-4 m-4 bg-blue-300'>
      <div className="max-w-md mx-auto p-2">
        <div className="relative flex items-center">
          <input
            type="search"
            id="default-search"
            className="block w-full p-2 text-sm text-gray-900 border border-gray-300 rounded-lg placeholder-gray-400 focus:outline-none"
            placeholder="Search city here..."
            value={city}
            onChange={(e) => setCity(e.target.value)}
            required
          />
          <button
            type="button"
            className="text-white bg-green-500 hover:bg-blue-500 font-medium rounded-lg text-sm px-4 py-2 ml-2"
            onClick={() => {
              if (city.trim() !== '') {
                setCity(city.trim());
              }
            }}
          >
            Search
          </button>
        </div>
      </div>

      {weather && (
        <div>
          <h2>Weather in {weather.name}</h2>
          <p>Temperature: {weather.main.temp}Â°C</p>
          <p>Description: {weather.weather[0].description}</p>
        </div>
      )}
    </div>
  );
}

export default Three;
